import React, { Component } from 'react';
import { Layout, Navigation, Content } from 'react-mdl';
import { Link } from 'react-router-dom';
import "fullpage.js/vendors/scrolloverflow"; // Optional. When using scrollOverflow:true
import ReactFullpage from "@fullpage/react-fullpage";
import PCtechnical from './images/PC-Technical.jpg';
import LogoPC from './images/pcIconN.png';
import TwitterPh from './images/TwitterPh.png';

class Resume extends Component {
  render() {
    return (
      <ReactFullpage
        fitToSection = {true}
        sectionsColor = {["black", "black", "red"]}
        scrollOverflow = {true}
        render={({ state, fullpageApi }) => {
          return (
            <div id="fullpage-wrapper">
              <div className="section fp-auto-height" style={{backgroundImage: "url('https://cdn.shopify.com/s/files/1/0039/5565/4765/products/SMS_A2_cutting_mat_black_1024x1024.jpg?v=1574160911')",
              objectFit: "cover", backgroundSize: "cover", overflow: "hidden", height:'100%'}}>
                <div className = "MainFormat">
                  <img src={LogoPC} style={{height: '28vh'}}/>
                  <h1 className = "Tittle">UX / UI Designer</h1>
                  <p className = "SubTittle">"Design it till you make it"</p>
                    <div >
                      <p href="url" style={{display: 'inline'}}> <a className="LinkStyle" href="mailto:pablocg1995@gmail.com">
                          <i class="fa fa-envelope fa-lg" style={{color: '#8b8b8b', fontSize:'3vh', padding: '1.5vh'}}></i></a></p>
                      <p style={{display: 'inline'}}><a className="LinkStyle" href="tel:+34676328005">
                          <i class="fa fa-phone fa-lg" style={{color: '#8b8b8b ', fontSize:'3vh', padding: '1.5vh'}}></i></a></p>
                      <p style={{display: 'inline'}}><a className="LinkStyle" href="https://www.linkedin.com/in/p-canton/">
                          <i class="fa fa-linkedin fa-lg" style={{color: '#8b8b8b', fontSize:'3vh', padding: '1.5vh'}}></i></a></p>
                      <p style={{display: 'inline'}}><a className="LinkStyle" href="https://github.com/p-canton">
                          <i class="fa fa-github fa-lg" style={{color: '#8b8b8b', fontSize:'3vh', padding: '1.5vh'}}></i></a></p>
                    </div>
                    <div style={{paddingTop:'4vh', paddingBottom:'4vh'}}>
                      <Link className="LinkStyle"  onClick={() => fullpageApi.moveTo(2, 0)}style={{paddingRight:'4vh'}}>
                        <p className="MainButton"style={{color: 'black', fontSize:'3vh'}}><b>Projects</b></p></Link>
                      <Link className="LinkStyle" to="/PC-folio.github.io/projects" style={{paddingLeft:'4vh'}}>
                        <p className="MainButton"style={{color: 'black', fontSize:'3vh'}}>Resume</p></Link>
                    </div>
                  </div>
                </div>
              <div className="section">
              <div className = "ContGrid2" style={{color: 'white', padding: '1em', paddingTop: '2em'}}>
                <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                <img src={TwitterPh} style={{maxWidth: '30vh', width: '80%', height: 'auto'}}/></div>
                <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                  <div><p style={{fontSize: '1.5em'}}><b>UI Tools</b></p>
                  <p style={{fontSize: '1em', lineHeight: '80%'}}>WebFlow</p>
                  <p style={{fontSize: '1em', lineHeight: '80%'}}>Figma / Framer X</p>
                  <p style={{fontSize: '1em', lineHeight: '80%'}}>Balsamiq</p></div></div>
                </div>
              </div>
              <div className="section fp-auto-height">
                <span className="slide">
                  <h3>Slide 2.1</h3>
                  <button onClick={() => fullpageApi.moveTo(3, 0)}>
                    Move top
                  </button>
                </span>
                <span className="slide">
                  <h3>Slide 2.2</h3>
                </span>
                <span className="slide">
                  <h3>Slide 2.3</h3>
                </span>
              </div>
            </div>
          );
        }}
      />
    );
  }
}




export default Resume;
