import React, { Component } from 'react';
import { Tabs, Tab, Button} from 'react-mdl';
import Posti from './images/Posti.jpg';
import HackathonGOC from './images/HackathonGOC.jpg';
import CodeWeek from './images/CodeWeek.jpg';
import Adidas from './images/Adidas.jpg';
import BetsFriends from './images/BetsFriends.jpg';
import Vredestein from './images/Vredestein.jpg';
import PCdesign from './images/PC-Design.jpg';
import PCtechnical from './images/PC-Technical.jpg';
import { Layout, Navigation, Content } from 'react-mdl';
import { Link } from 'react-router-dom';
import { FullPage, Slide } from 'react-full-page';

class Projects extends React.Component {
  constructor(props) {
    super(props);

    this.fullPageRef = React.createRef();
  }

  onControlsClick = () => {
    this.fullPageRef.current.scrollToSlide(1);
  }

  render() {
    const baseStyle = {
      alignItems: 'center',
      display: 'flex',
      justifyContent: 'center',
    };

    return (
      <div className="container">
        <button
          style={{ position: 'fixed' }}
          onClick={this.onControlsClick}
          type="button"
        >
          Controls
        </button>
        <FullPage ref={this.fullPageRef}>
          <Slide
            style={{
              ...baseStyle,
              background: '#2ECC40',
            }}
          >
            <h1>Custom Controls</h1>
          </Slide>
          <Slide
            style={{
              ...baseStyle,
              background: '#0074D9',
            }}
          >
            <h1>2</h1>
          </Slide>
          <Slide
            style={{
              ...baseStyle,
              background: '#00c4ff',
            }}
          >
            <h1>3</h1>
          </Slide>
          <Slide
            style={{
              ...baseStyle,
              background: '#d52685',
            }}
          >
            <h1>4</h1>
          </Slide>
        </FullPage>
      </div>
    );
  }
}

export default Projects;

